
public class Zweierpotenzen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
    int[] potenzen  = new int [19];
    int x =1 ;
    System.out.println(x);
    for (int i=0; i<potenzen.length;i++)
    {
	x=x*2;
	potenzen[i]=x;
	
    System.out.println(potenzen[i]);
    }
    }
}